import { BrowserRouter as Router, Route } from 'react-router-dom';
import ArtistDetailts from './components/ArtistDetailts';
import ListOfMathcingResults from './components/ListOfMathcingResults';
import MainAppBar from './components/AppBar'
import './App.css';

function App() {
  useEffect(() => {
    const getSerchedArtists = async () => {

      const serch_term = {
        term: 'eminem'
    };
       const requestOptions = {
          method: 'POST',
          headers: {
              'Content-Type': 'application/json'
          },
          body: JSON.stringify(serch_term)

      };
      try {
          const response = await fetch('http://localhost:5000/api/artist_details', requestOptions);
          if (response.ok) {
            const data = await response.json();
            console.log(data);
            return data
        } else {
          const message = `An error has occured: ${response.status}`;
          throw new Error(message);
        }

      } catch (e) {
          return e;
      }    

  }

