import { combineReducers } from 'redux';

import {
  SEARCH_ARTISTS,
  SEARCH_ALBUMS,
  ARTISTS_ERROR,
  ALBUMS_ERROR,
  //LOADING_ARTISTS,
  //LOADING_ALBUMS

} from '../actions/types';

// Initial state
const artistsState = {
  artists_list: [],
  error: null,
  loading: true
};


// Initial state
const albumsState = {
  albums: [],
  albulms_error: null,
  loading_albums: true
};

/**
 * 
 * @param {*} state 
 * @param {*} action 
 */

const artists = (state = artistsState, action) => {
  switch (action.type) {
    case SEARCH_ARTISTS:
      return {
        ...state,
        loading: false,
        artists_list: [action.payload]
      };

    case ARTISTS_ERROR:
      return {
        ...state,
        error: action.payload
      };

    default:
      return state;
  }
};

const albums = (state = albumsState, action) => {
  switch (action.type) {
    case SEARCH_ALBUMS:
      return {
        ...state,
        loading_albums: false,
        albums: action.payload
      };

    case ALBUMS_ERROR:
      return {
        ...state,
        albums_error: action.payload
      };

    default:
      return state;
  }
};


export default combineReducers({
  artists,
  albums
});
