import React, {useState} from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {searchAlbums} from '../actions/actions';
import { Link, RouteComponentProps } from "react-router-dom";
import Loading from './Loading';
import NothingToShow from './NothingToShow';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';



const Albums:React.FC = (props) => {
  const classes = useStyles();
  const dispatch = useDispatch()
  const [term] = useState(props.artist_name)
  let albums_results = useSelector(state => state.artists.albums);
  let loading = useSelector(state => state.artists.loading_albums);
  let albums_error = useSelector(state => state.artists.albums_error);

// searching for albums of the artist
  const addSearchTerm = (event) => {
    event.preventDefault()
    dispatch(searchAlbums({
      term
    }))
  }



  if (albums_error) {
    return <Typography variant="h1" color="primary">Error! Failed to seach or loadÂ albums!</Typography>
  }

  return (
    <div>
      {loading ? (
        <Loading />
      ) : (
          <div>

            { albums_results.length === 0 || !albums_results ?
              <NothingToShow />
              :
              <List className={classes.root}>
                {
                  albums_results.map((item, key) => (
                    <div className="list_item_container">

                    <ListItem
                      className={classes.listItem}
                      key={item.id}
                      alignItems="center"
                      divider
                      button
                    >

                      <img src={item.cover_small} alt="img" />
                      <ListItemText primary={`Number of tacks: ${item.title}`} />
                      <ListItemText primary={`Fans: ${item.nb_fan}`} />
                    </ListItem>
                    </div>
                  ))
                }
              </List>
            }
          </div>
        )}
    </div>
  );
}

export default Albums;
