import * as React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Typography from '@material-ui/core/Typography';
import { Link, RouteComponentProps} from 'react-router-dom';
import ArtistCard from './ArtistCard';
import Albums from './Albums';

type TParams = { artist_id: number };


const ArtistDetailts: React.FC = ({ match }: RouteComponentProps<TParams>) => {

  let artists_results = useSelector(state => state.artists.artists_list);
  const detailed_artist_array = artists_results.filter(artists_item => artists_item.id == match.params.artist_id)


  return (
    <div>
      <ArtistCard detailed_artist_array={detailed_artist_array} />
      <Albums artist_name={detailed_artist_array[0].name} /> 

    </div>
  );
}

export default ArtistDetailts;
